import './App.css';
import React, { useState } from 'react';
import axios from 'axios';
import { response } from 'express';
import Wheather from './Wheater';

function App() {
  const [data,setData] = useState({})
  const [location, setLocation] = useState('')

  const url = 'https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=38444bd806c43f5e16da1714f978e46e'

  const searchLocation = (event) => {
    if (event.key == 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }
  }

  return (
    <div className='App'>
      <div className='search'>
        <input 
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyDownCapture={searchLocation}
          placeholder='Enter location'
          type="text"/>
      </div>
      <div className='container'>
        <div className='top'>
          <div className='location'>
            <p>{data.name}</p>
          </div>
          <div className='temp'>
            {data.main ? <h1>{data.main.temp}</h1> : null}
            <h1>{data.main.temp}°C</h1>
          </div>
          <div className='description'>
            <img src={'http://openwheatermap.org/img/wn/${weatherData.wheater[0].con}@2x.png'}/>
          </div>
        </div>

        {data.name != undefined &&
        <div className='bottom'>
          <div className='ressenti'>
            {data.main ? <p className='bold'>{data.main.feels_like}°C</p> : null}
            <p>Ressenti</p>
          </div>
          <div className='humiditer'>
            {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
            <p>Humiditer</p>
          </div>
          <div className='vent'>
            {data.wind ? <p className='bold'>{data.main.speed}Km/H</p> : null}
            <p>Vitesse du vent</p>
          </div>
        </div>
      }

      </div>
    </div>
  );
}

export default App;
